import React, { useState } from "react";
import Swal from "sweetalert2";
import { Item } from "./Item";
import { ItemCount } from "./ItemCount";
import axios from 'axios';

export const ItemList = ({ products, categories,authTokens}) => {
  const [cartItems,setCartItems]=useState({});

    const addItemToCart = (data) => {
      axios
        .post("http://localhost:8080/api//user/cart/addtocart", data)
        .then((result) => {
          console.log("result", result);
          if (result.status === 200) {
            setCartItems(result.data);
            console.log(result.data);
          } 
        })
        .catch((e) => {
          console.log(e);
        });
    };


  const onAdd = (count, idItem) => {
    const Toast = Swal.mixin({
      toast: true,
      position: "top-end",
      showConfirmButton: false,
      timer: 1500,
      timerProgressBar: true,
      didOpen: (toast) => {
        toast.addEventListener("mouseenter", Swal.stopTimer);
        toast.addEventListener("mouseleave", Swal.resumeTimer);
      },
    });

    Toast.fire({
      icon: "success",
      title: `${count} items/s Added`,
    });


   /* cartItems: [
      {
          product: { type: mongoose.Schema.Types.ObjectId, ref: 'Product', required: true },
          quantity: { type: Number, default: 1 },
          price:{type:Number,required: true}
      }*/
    addItemToCart({
      name: products.find((prod) => prod.id === idItem).name,
      price: products.find((prod) => prod.id === idItem).price,
      quantity: count,
      subTotal: count * products.find((prod) => prod.id === idItem).price,
    });
  };

  return (
    <div>
      {categories.map((category) => (
        <>
          <div className="ml-2">
            <br />
            <h1 key={category.id}>{category.name}:</h1>
            <br />
          </div>

          <div className="containerProducts d-flex flex-wrap justify-content-center">
            {products
              .filter((product) => product.category.name === category.name)
              .map((product) => (
                <>
                  <div
                    key={product.id}
                    className="product-card card bg-light me-1 ms-1 mb-3 rounded-3 p-2"
                  >
                    <Item
                      key={product.id}
                      img={product.productImage}
                      name={product.name}
                      description={product.descripcion}
                      category={product.category.name}
                      price={product.price}
                    />
                    <ItemCount
                      key={product.id}
                      initial={1}
                      id={product.id}
                      onAdd={onAdd}
                    />
                  </div>
                </>
              ))}
          </div>
        </>
      ))}
    </div>
  );
};
