import React, { memo } from "react";
import Swal from "sweetalert2";
import { useCartContext } from "../context/CartContext";
import { Item } from "./Item";
import { ItemCount } from "./ItemCount";

// callback             callback con comparacion
// memo(() => {}) || memo( ()=>{}, ()=>{})

export const ItemList = memo(
  ({ products, categories }) => {
    const { addToCart } = useCartContext();

    /*   const onAdd = (count, idItem) => {
        const Toast = Swal.mixin({
            toast: true,
            position: 'top-end',
            showConfirmButton: false,
            timer: 1500,
            timerProgressBar: true,
            didOpen: (toast) => {
                toast.addEventListener('mouseenter', Swal.stopTimer)
                toast.addEventListener('mouseleave', Swal.resumeTimer)
            }
        })

        Toast.fire({
            icon: 'success',
            title: `Se agregó ${count} items/s`
        })

        // console.log(productos.find(prod => prod.idNumber === idItem).idNumber);

        addToCart({
            id: products.find(prod => prod.idNumber === idItem).idNumber,
            name: products.find(prod => prod.idNumber === idItem).title,
            price: products.find(prod => prod.idNumber === idItem).precio,
            cantidad: count,
            subTotal: count * products.find(prod => prod.idNumber === idItem).precio
        })
    }*/

    // console.log
    console.log(products);
    // console.log

    return (
      <div>
        {categories.map((category) => (
          <>
            <div className="ml-2">
                <br />
                <h1 key={category.id}>{category.name}:</h1>
                <br/>
            </div>

            <div className="containerProducts d-flex flex-wrap justify-content-center">
            {products
              .filter((product) => product.category.name === category.name)
              .map((product) => (
                <>

                  <div
                    key={product.id}
                    className="product-card card bg-light me-1 ms-1 mb-3 rounded-3 p-2"
                  >
                    <Item
                      key={product.id}
                      img={product.productImage}
                      name={product.name}
                      description={product.descripcion}
                      category={product.category.name}
                      price={product.price}
                    />
                    <ItemCount
                      key={product.id}
                      initial={1}
                      id={product.id}
                      idNumber={product.idNumber}
                    />
                  </div>

                </>
              ))}
            </div>

          </>
        ))}
      </div>
    );
  },
  (prevProp, nextProp) =>
    prevProp.productos.length === nextProp.productos.length
);
