import React, { useState } from "react";
import { addtocart } from "../api";
import Swal from "sweetalert2";

export const Item = ({
  productId,
  img,
  category,
  description,
  name,
  price,
}) => {
  const initial = 1;
  const [countItem, setCountItem] = useState(initial);
  const [stockItem] = useState(15);
  const addItemToCart = async (data) => {
    const response = await addtocart(data);
    if (response.status === 201) {
      console.log(response.data);
    } else {
      console.log(response.message);
    }
  };

  const onAdd = (count, id) => {
    const Toast = Swal.mixin({
      toast: true,
      position: "top-end",
      showConfirmButton: false,
      timer: 1500,
      timerProgressBar: true,
      didOpen: (toast) => {
        toast.addEventListener("mouseenter", Swal.stopTimer);
        toast.addEventListener("mouseleave", Swal.resumeTimer);
      },
    });

    Toast.fire({
      icon: "success",
      title: `${count} items/s Added`,
    });

    /* cartItems: [
          {
              product: { type: mongoose.Schema.Types.ObjectId, ref: 'Product', required: true },
              quantity: { type: Number, default: 1 },
              price:{type:Number,required: true}
          }*/

    addItemToCart({
      cartItems: { product: id, quantity: count, price: price },
    });
  };
  /* INCREMENTO DE ITEM */
  const increaseCount = () => {
    if (countItem >= stockItem) {
      setCountItem(stockItem);
    } else {
      setCountItem(countItem + 1);
    }
  };

  /* DECREMENTO DE ITEM */
  const decreaseCount = () => {
    if (countItem <= initial) {
      setCountItem(initial);
    } else {
      setCountItem(countItem - 1);
    }
  };

  const addItem = (count, id) => {
    onAdd(count, id);
    setCountItem(initial);
  };
  const add =  (id) => {
    addItemToCart({
      cartItems: {
        product: `&{productId}`,
        quantity: 3,
        price: 15,
      },

    }

    );
    console.log(productId);
  };
  return (
    <>
      <img
        src={img}
        className="img-items card-img-top d-flex justify-content-center"
        alt="..."
      />
      <div className="card-body text-center">
        <h5 className="card-title fw-bold">{name}</h5>
        <p className="description-card card-text fw-normal">{description}</p>
        <h5 className="price fw-bold mt-2">Price: $ {price}</h5>
      </div>
      <div className="card-footer bg-light text-center">
        <div className="d-flex justify-content-between align-items-center p-2 m-2 border rounded">
          <button
            onClick={decreaseCount}
            className="btn btn-secondary fw-bold fs-5 ps-3 pe-3"
          >
            -
          </button>
          <span>{countItem}</span>
          <button
            onClick={increaseCount}
            className="btn btn-secondary fw-bold fs-5 ps-3 pe-3"
          >
            +
          </button>
        </div>
        <div className="d-flex justify-content-around">
          <button
            onClick={() => add(productId)}
            className="btn btn-success p-1"
          >
            Add to cart
          </button>
        </div>
      </div>
    </>
  );
};
