          <Route exact path="/" element={<HomeContainer />} />
          <Route exact path="/products" element={<ItemListContainer />} />
          <Route exact path="/cart" element={<CartContainer />} />
          <Route exact path="/auth" element={<AuthContainer />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
};

export default App;
