import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../auth";

export default function AuthContainer({ authTokens }) {
  
  let [authMode, setAuthMode] = useState("signin");
  const initialState = {
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  };
  const [form, setForm] = useState(initialState);
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [isError, setIsError] = useState(false);
  const { setAuthTokens } = useAuth();
  const history = useNavigate();
const token=localStorage.getItem("tokens");
  let axiosConfig = {
    headers: {
      Authorization: `Bearer ${Cookies.get('token')}`,
    },
  };
  const signIn = (data) => {
    axios
      .post("http://localhost:8080/api/signIn", data, axiosConfig)
      .then((result) => {
        console.log("result", result);
        if (result.status === 200) {
          setAuthTokens(result.data);
          setLoggedIn(true);
          console.log(result.data.token);
        } else {
          setIsError(true);
        }
      })
      .catch((e) => {
        setIsError(true);
        console.log(e);
      });
  };

  const signUp = (data) => {
    axios
      .post("http://localhost:8080/api/signUp", data,axiosConfig)
      .then((result) => {
        console.log("result", result);
        if (result.status === 200) {
          setAuthTokens(result.data);
          setLoggedIn(true);
          console.log(result.data);
        } else {
          setIsError(true);
        }
      })
      .catch((e) => {
        setIsError(true);
        console.log(e);
      });
  };

  const handlechange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (event) => {
    event.preventDefault();
    if (authMode === "signup") {
      signUp(form);
    } else {
      signIn(form);
    }
  };

  if (isLoggedIn) {
    return history("/");
  }

  const changeAuthMode = () => {
    setAuthMode(authMode === "signin" ? "signup" : "signin");
  };

  if (authMode === "signin") {
    return (
      <div className="Auth-form-container mx-auto" style={{ width: "400px" }}>
        <form className="Auth-form " onSubmit={handleSubmit}>
          <div className="Auth-form-content">
            <h3 className="Auth-form-title ">Sign In</h3>

            <div className="form-group mt-3">
              <label>Email address</label>
              <input
                type="email"
                name="email"
                className="form-control mt-1"
                placeholder="Enter email"
                onChange={handlechange}
              />
            </div>
            <div className="form-group mt-3">
              <label>Password</label>
              <input
                type="password"
                name="password"
                className="form-control mt-1"
                placeholder="Enter password"
                onChange={handlechange}
              />
            </div>
            <div className="d-grid gap-2 mt-3">
              <button type="submit" className="btn btn-primary">
                Submit
              </button>
            </div>
            <div className="text-center">
              Not registered yet?{" "}
              <span className="link-primary" onClick={changeAuthMode}>
                Sign Up
              </span>
            </div>
          </div>
        </form>
      </div>
    );
  }

  return (
    <div className="Auth-form-container mx-auto " style={{ width: "400px" }}>
      <form className="Auth-form" onSubmit={handleSubmit}>
        <div className="Auth-form-content">
          <h3 className="Auth-form-title">Sign Up</h3>

          <div className="form-group mt-3">
            <label>First Name</label>
            <input
              type="text"
              name="firstName"
              className="form-control mt-3"
              placeholder="e.g Jane Doe"
              onChange={handlechange}
            />
          </div>
          <div className="form-group mt-3">
            <label>Last Name</label>
            <input
              type="text"
              name='lastName'
              className="form-control mt-3"
              placeholder="e.g Jane Doe"
              onChange={handlechange}
            />
          </div>
          <div className="form-group mt-3">
            <label>Email address</label>
            <input
              type="email"
              name="email"
              className="form-control mt-3"
              placeholder="Email Address"
              onChange={handlechange}
            />
          </div>
          <div className="form-group mt-3">
            <label>Password</label>
            <input
              type="password"
              name="password"
              className="form-control mt-3"
              placeholder="Password"
              onChange={handlechange}
            />
          </div>
          <div className="d-grid gap-2 mt-3">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
          <div className="text-center mt-3">
            Already registered?{" "}
            <span className="link-primary" onClick={changeAuthMode}>
              Sign In
            </span>
          </div>
        </div>
      </form>
    </div>
  );
}
