import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { TableCart, TableCartFoot } from "../TableCart";
import { getCartItems, removeItem } from "../../api";

export const CartContainer = () => {
  const [cartList, setCartList] = useState({});

  const getCart = async () => {
    const response = await getCartItems();
    setCartList(response.data);
    // console.log(cartList?.cartItems.length);
  };
  const clearItem = async() => {
    const response=await removeItem("646692deb6a3853b94b012b1");
    if(response.status===201){
      console.log("deleted successfully");
    }
    else{console.log("theres error in deleting" )}
  }
  useEffect(() => {
    getCart();
  }, []);

  return (
    <div className="mt-5 pt-5">
      <div className="mt-5">
        <div className="container d-flex flex-column align-items-center">
          <h1 className="fw-bold mb-2">~ Chart ~</h1>
          {cartList?.cartItems && cartList?.cartItems.length === 0 ? (
            <>
              <h2 className="text-danger">Le panier est vide</h2>
              <box-icon
                class="cart-empty"
                name="cart-download"
                type="solid"
                animation="tada"
                size="cssSize"
              ></box-icon>
              <Link to="/products" className="btn btn-primary">
                do your shopping
              </Link>
            </>
          ) : (
            <>
              <table class="table">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">Name</th>
                    <th scope="col">unit price</th>
                    <th scope="col">quantity</th>
                    <th scope="col">Sub total</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>
                  {
                    // cartList.map(e => console.log(e))
                    cartList?.cartItems && cartList?.cartItems.map((prod) => (

                      <> <TableCart
                        item={prod._id}
                        name={prod.name}
                        quantity={prod.qty}
                        price={prod.price}
                        subTotal={prod.price * prod.qty}
                        cartList={cartList}
                        setCartList={setCartList}
                      />

                        <button className="btn btn-danger" onClick={() => clearItem(prod._id)}>
                          Empty Chart
                        </button>
                      </>
                    ))
                  }
                </tbody>
                <tfoot>{<TableCartFoot /*total={totalCart()*/ />}</tfoot>
              </table>
              <div className="container d-flex justify-content-around">
                <Link to="/products" className="btn btn-primary">
                  Continue shopping
                </Link>
                <button className="btn btn-danger" >
                  Empty Chart
                </button>
                <button className="btn btn-success">Check out</button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};
