import React from 'react'


export const TableCartFoot = ({total}) => {
    return (
        <>
            <tr>
                <th scope="row">Total</th>
                <td></td>
                <td></td>
                <td></td>
                <td className="fw-bold">$ {total}</td>
            </tr>
        </>
    )
}


export const TableCart = ({ item,name, quantity, price, subTotal,cartList,setCartList }) => {

   const  clearItem = () =>{
    setCartList( cartList.filter(item => item.id !== id ));
   }

    return (
        <>
            <tr>
                <th scope="row">{id}</th>
                <td className="fw-bold text-capitalize">{name}</td>
                <td>{price}</td>
                <td>{quantity}</td>
                <td>${subTotal}</td>
                <td>
                    <button className="btn btn-danger p-0 d-flex align-items-center" onClick={()=>clearItem(item)}>
                        <box-icon name='x'></box-icon>
                    </button>
                </td>
            </tr>
        </>
    )
}
