import React, { createContext, useState, useContext } from "react"
import axios from 'axios';

const CartContext = createContext()

export const useCartContext = () => useContext(CartContext)

export const CartContextProvider = ({children}) => {

    const [cartList, setCartList] = useState([{}])

    const addItemToCart = (data) => {
        axios
          .post("http://localhost:8080/api/signIn", data, axiosConfig)
          .then((result) => {
            console.log("result", result);
            if (result.status === 200) {
              setAuthTokens(result.data);
              setLoggedIn(true);
              console.log(result.data.token);
            } else {
              setIsError(true);
            }
          })
          .catch((e) => {
            setIsError(true);
            console.log(e);
          });
      };

    function addToCart(items) {
        //[...cartList] (todo lo que tiene adentro lo pega dentro del nuevo array)

        const cartListX = [...cartList];

		if (cartListX.find(prod => prod.id === items.id)) {

            // sumo el subtotal nuevo al valor anterior
            cartListX.find(prod => prod.id=== items.id).subTotal += items.quantity*items.price

            // sumo la cantidad nueva al valor anterior
            cartListX.find(prod => prod.id === items.id).quantity += items.quantity;

			setCartList(cartListX);


        } else {
            setCartList([
                ...cartList,
                items
            ])
        }
    }

    //precio total
    const totalCart = () => {
		return cartList.reduce((total, items) => total + (items.quantity * items.price) ,0)
	}

    //items totales en carrito
    const CartItems = () => {
		return cartList.reduce((prev, next) => prev + next.quantity, 0)
	}

    //vaciar carrito
    const clear = () => {
		setCartList([]);
	};

    // eliminar item
    const clearItem = (id) => {
		setCartList( cartList.filter(item => item.id !== id ));
	};

    return (
        <CartContext.Provider value={{
            cartList,
            addToCart,
            totalCart,
            CartItems,
            clear,
            clearItem
        }}>
            {children}
        </CartContext.Provider>
    )
}
